import React, { useEffect, useState} from 'react';
import Items from "./Items";
import NewPost from "./NewPost";
import Header from "./Header";

const List = ({list,setList, setIsAuth, loginId ,setLoginId, mail}) => {


    const [sortList, setSortList] = useState([])
    const [sort, setSort] = useState("all");
    const [value, setValue] = useState({
        text: "",
        completed: false,
        user_id: null
    });

    useEffect(() => {
        const list_sorted = async () => {
            if(sort === "all"){
                try {
                    const response = await fetch(`api/post/${loginId}`)
                    const data = await response.json();
                    setSortList(data.rows);
                    console.log(sortList);
                }catch(err){
                    console.log(err)
                }
            }
            if (sort === "completed"){
                try {
                    const response = await fetch(`api/post/true/${loginId}`)
                    const data = await response.json();
                    setSortList(data.rows);
                    console.log(sortList);
                }catch(err){
                    console.log(err)
                }
            }
            if (sort === "not-completed"){
                try {
                    const response = await fetch(`api/post/false/${loginId}`)
                    const data = await response.json();
                    setSortList(data.rows);
                    console.log(sortList);
                }catch(err){
                    console.log(err)
                }
            }
        }
        list_sorted();
    },[sort, loginId])


    const appendToDoList = async () => {
        try{
            const response = await fetch('api/post',{
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(value)
            })
            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.message || 'Site failed');
            }
            setList([...list, value])
            setValue({text:"", completed: false,user_id: null})
        }catch(err){
            console.error(err)
        }
    }

    const deleteItem = (index) => {
        setList(list.filter(el => el.id !== index))
    }

    return (
        <div>
            <Header mail={mail} setSort={setSort} setIsAuth={setIsAuth} setLoginId={setLoginId}/>
            <Items loginId={loginId} list={list} sortList={sortList} deleteItem={deleteItem} setList={setList} sort={sort}/>
            <NewPost loginId={loginId} value={value.text} setValue={setValue} appendToDoList={appendToDoList}/>
        </div>
    );
};

export default List;
