import React, { useEffect, useState} from 'react';
import Items from "./Items";
import NewPost from "./NewPost";
import Header from "./Header";

const List = (props) => {

    const [list, setList] = useState([
        {id : 1, text: "Встать",completed: false},
        {id : 2, text : "Умыться",completed: false},
        {id: 3, text: "Спать", completed: false},
        {id: 4, text: "Есть", completed: false},
    ]);

    const [sortList, setSortList] = useState([])

    const [sort, setSort] = useState("all");

    useEffect(() => {
        if(sort === "all"){
            setSortList([...list])
        }
        if (sort === "completed"){
            setSortList(list.filter(item => item.completed))
        }
        if (sort === "not-completed"){
            setSortList(list.filter(item => !item.completed))
        }
    },[sort,list])


    const [value, setValue] = useState({
        id: null,
        text: "",
        completed: false
    });

    const appendToDoList = () => {
        setList([...list, value])
        setValue({id:null , text:"", completed: false})
    }

    const deleteItem = (index) => {
        setList(list.filter(el => el.id !== index))
    }

    return (
        <div>
            <Header setSort={setSort} />
            <Items list={list} sortList={sortList} deleteItem={deleteItem} setList={setList} sort={sort}/>
            <NewPost count = {list.length} value={value.text} setValue={setValue} appendToDoList={appendToDoList}/>
        </div>
    );
};

export default List;
