import React from 'react';
import Item from "./Item";

const Items = (props) => {

    const filteredList = props.list.filter((item) => {
        if (props.sort === "all") return true;
        if (props.sort === "completed") return item.completed;
        if (props.sort === "not-completed") return !item.completed;
        return true;
    })

    return (
        <div className="List">`
        {props.list.map((item,id) => (
            <Item key={item.id} list={props.list} setList={props.setList} item={item} id={id} deleteItem={props.deleteItem} />
            ))}
        </div>
    );
};

export default Items;
