import React, {useEffect, useState} from 'react';
import List from "./components/List";
import "./styles/app.css"
import Login from "./components/Login";

const App = () => {
    const [isAuth, setIsAuth] = useState(false);
    const [loginId, setLoginId] = useState(null);
    const [mail, setMail] = useState(null);

    const [trigger,setTrigger] = useState(0);
    const [list, setList] = useState([])
    const [sort, setSort] = useState("all");

    useEffect(() => {
        const list_sorted = async () => {
            try {
                let url = `api/post/${loginId}`;
                if (sort === "completed") url = `api/post/true/${loginId}`;
                if (sort === "not-completed") url = `api/post/false/${loginId}`;

                const response = await fetch(url);
                const data = await response.json();
                setSortList(data.rows);
            } catch(err) {
                console.log(err);
            }
        }
        list_sorted();
    }, [sort, loginId, trigger]);

    return(
    <div>
            {isAuth ?
                <List sort={sort} setSort={setSort} mail={mail} setIsAuth={setIsAuth} loginId={loginId} setLoginId={setLoginId}/>
                :
                <Login setMail={setMail} setLoginId={setLoginId} setIsAuth={setIsAuth}/>
            }
    </div>

    );
};

export default App;
