import React, {useState} from 'react';
import MyInput from "./UI/input/MyInput";
import MyButton from "./UI/button/MyButton";

const List = (props) => {

    const [list, setList] = useState([
        "1. Встать",
        "2. Поесть",
        "3. Учиться",
    ]);

    const [value, setValue] = useState("");

    const appendToDoList = () => {
        setList([...list, value])
        setValue("")
    }

    return (
        <div>
            {list.map((item) => (
                <p>{item}</p>
            ))}
            <MyInput type="text" placeholder="Добавить дело" onChange={e => setValue(e.target.value)} />
            <MyButton type="button" onClick={appendToDoList}>Добавить пункт</MyButton>
        </div>

    );
};

export default List;
