import React, {useState} from 'react';
import Item from "./Item";

const Items = (props) => {

    return (
        <div className="List">`
        {props.sortList.map((item,id) => {
            {item.user_id === props.loginId ? <Item key={item.id} list={props.list} setList={props.setList} item={item} id={id}
                                                    deleteItem={props.deleteItem}/>
            : null
            }

        })}
        </div>
    );
};

export default Items;
