import React, {useState} from 'react';
import Items from "./Items";
import NewPost from "./NewPost";

const List = () => {

    let [list, setList] = useState([
        {id : 1, text: "Дрочить хуй"},
        {id : 2, text : "Ебаться в жопу"}
    ]);

    const [value, setValue] = useState("");

    const appendToDoList = () => {
        setList([...list, value])
        setValue("")
    }

    const deleteItem = (item) => {
        setList(list.filter(it => it !== item))
    }

    return (
        <div>
            <Items list={list} deleteItem={deleteItem} setList={setList} />
            <NewPost value={value} setValue={setValue} appendToDoList={appendToDoList}/>
        </div>
    );
};

export default List;
