import React, {use, useEffect, useState} from 'react';
import Items from "./Items";
import NewPost from "./NewPost";
import Header from "./Header";

const List = (props) => {

    let [list, setList] = useState([
        {id : 1, text: "Дрочить хуй",completed: false},
        {id : 2, text : "Ебаться в жопу",completed: false},
    ]);

    const [sort, setSort] = useState("all");

    useEffect(() => {
        if(sort === "all"){
            const sort_list = [...list];
            console.log(sort_list);
        }
        else if(sort === "completed"){
            const sort_list = [list.filter(el => el.completed === true)];
            console.log(sort_list);
        }

        else if(sort === "not-completed"){
            const sort_list = [list.filter(el => el.completed === false)];
            console.log(sort_list);
        }
    }, [sort]);



    const [value, setValue] = useState({
        id: null,
        text: "",
    });

    const appendToDoList = () => {
        setList([...list, value])
        setValue({id:null , text:""})
    }

    const deleteItem = (index) => {
        setList(list.filter(el => el.id !== index))
    }

    return (
        <div>
            <Header setSort={setSort} />
            <Items list={list} deleteItem={deleteItem} setList={setList} />
            <NewPost count = {list.length} value={value.text} setValue={setValue} appendToDoList={appendToDoList}/>
        </div>
    );
};

export default List;
