import React from 'react';

const Login = (props) => {
    const [email, setEmail] = React.useState('');
    const [password, setPassword] = React.useState('')
    const [reg, setReg] = React.useState({email: "", password: ""});
    const [error, setError] = React.useState(false);
    const [regError, setRegError] = React.useState(false);

    const login = () => {
        // Заменяем map на some (для проверки существования пользователя)
        const userExists = props.users.some(user => {
            if (user.email === email && user.password === password) {
                props.setLoginId(user.id);
                props.setMail(email);
                setEmail("");
                setPassword("");
                props.setIsAuth(true);
                return true;
            }
            return false;
        });

        if (!userExists) {
            setError(true);
            setRegError(false);
            setEmail("");
            setPassword("");
        }
    }

    const register = async () => {
        // Заменяем map на some (для проверки существования email)
        const emailExists = props.users.some(user => user.email === reg.email);

        if (emailExists) {
            setRegError(true);
            setError(false);
            return;
        }

        try {
            const response = await fetch('/api/user', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(reg),
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.message || 'Registration failed');
            }

            props.setUsers([...props.users, {id: props.countUsers, email: reg.email, password: reg.password}]);
            props.setMail(reg.email);
            props.setLoginId(props.countUsers);
            props.setCountUsers(props.countUsers + 1);
            setReg({email: "", password: ""});
            props.setIsAuth(true);
            setRegError(false);
        } catch (err) {
            console.error('Registration error:', err);
            setRegError(true);
        }
    }

    return (
        <div className="Lgn">
            <div>
                <h1 style={{textAlign: "center", color: "white"}}>Login</h1>
                <form className="form">
                    <input
                        type="text"
                        className="inpFrm"
                        value={email}
                        placeholder="Email"
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <input
                        type="password"
                        className="inpFrm"
                        value={password}
                        placeholder="Password"
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button type="button" className="btnFrm" onClick={login}>Login</button>
                    {error && (
                        <div style={{color: "red", fontWeight: "bold"}}>НЕВЕРНЫЕ ДАННЫЕ</div>
                    )}
                </form>
            </div>
            <div>
                <h1 style={{textAlign: "center", color: "white"}}>Register</h1>
                <form className="form">
                    <input
                        type="text"
                        className="inpFrm"
                        value={reg.email}
                        placeholder="Email"
                        onChange={(e) => setReg({...reg, email: e.target.value})}
                    />
                    <input
                        type="password"
                        className="inpFrm"
                        value={reg.password}
                        placeholder="Password"
                        onChange={(e) => setReg({...reg, password: e.target.value})}
                    />
                    <button type="button" className="btnFrm" onClick={register}>Register</button>
                    {regError && (
                        <div style={{color: "red", fontWeight: "bold"}}>ТАКОЙ EMAIL УЖЕ ЗАРЕГИСТРИРОВАН</div>
                    )}
                </form>
            </div>
        </div>
    );
};

export default Login;
