import React from 'react';

const Login = (props) => {
    const [email, setEmail] = React.useState('');
    const [password, setPassword] = React.useState('')
    const [reg, setReg] = React.useState({email: "", password: ""});
    const [error, setError] = React.useState(false);
    const [regError, setRegError] = React.useState(false);

    const login = () => {
        const userExists = props.users.some(user => {
            if (user.email === email && user.password === password) {
                props.setLoginId(user.id);
                props.setMail(email);
                setEmail("");
                setPassword("");
                props.setIsAuth(true);
                return true;
            }
            return false;
        });

        if (!userExists) {
            setError(true);
            setRegError(false);
            setEmail("");
            setPassword("");
        }
    }

    const register = async () => {

        try {
            const checkResponse = await fetch('api/user');
            if (!checkResponse.ok) {
                throw new Error('Не удалось проверить существующих пользователей');
            }
            const checkData = await checkResponse.json();
            const emailExistsInDB = checkData.rows.some(user => user.email === reg.email);


            if (emailExistsInDB) {
                setRegError(true);
                setError(false);
                return;
            }

            const response = await fetch('/api/user', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(reg),
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.message || 'Registration failed');
            }

            props.setMail(reg.email);
            props.setLoginId(data.id);
            props.setCountUsers(props.countUsers + 1);
            setReg({email: "", password: ""});
            props.setIsAuth(true);
            setRegError(false);
        } catch (err) {
            console.error('Registration error:', err);
            setRegError(true);
        }
    }

    return (
        <div className="Lgn">
            <div>
                <h1 style={{textAlign: "center", color: "white"}}>Login</h1>
                <form className="form">
                    <input
                        type="text"
                        className="inpFrm"
                        value={email}
                        placeholder="Email"
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <input
                        type="password"
                        className="inpFrm"
                        value={password}
                        placeholder="Password"
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button type="button" className="btnFrm" onClick={login}>Login</button>
                    {error && (
                        <div style={{color: "red", fontWeight: "bold"}}>НЕВЕРНЫЕ ДАННЫЕ</div>
                    )}
                </form>
            </div>
            <div>
                <h1 style={{textAlign: "center", color: "white"}}>Register</h1>
                <form className="form">
                    <input
                        type="text"
                        className="inpFrm"
                        value={reg.email}
                        placeholder="Email"
                        onChange={(e) => setReg({...reg, email: e.target.value})}
                    />
                    <input
                        type="password"
                        className="inpFrm"
                        value={reg.password}
                        placeholder="Password"
                        onChange={(e) => setReg({...reg, password: e.target.value})}
                    />
                    <button type="button" className="btnFrm" onClick={register}>Register</button>
                    {regError && (
                        <div style={{color: "red", fontWeight: "bold"}}>ТАКОЙ EMAIL УЖЕ ЗАРЕГИСТРИРОВАН</div>
                    )}
                </form>
            </div>
        </div>
    );
};

export default Login;
