import React from 'react';
import SortSelected from "./SortSelected";
import MyButton from "./UI/button/MyButton";

const Header = (props) => {
    return (
        <div className="header">
            <SortSelected setSort={props.setSort}/>
            <h1 style={{color:"white", marginTop: "15px"}}>ToDoList</h1>
            {props.users.map((user) => {
                if(user.id === props.loginId){
                    <div style={{color: "white"}}>{user.email}</div>
                }
            })}
            <button className="exit" onClick={() => {props.setIsAuth(false)}}>Выйти</button>
        </div>
    );
};

export default Header;
