import React, {useState} from 'react';
import MyInput from "./UI/input/MyInput";
import MyButton from "./UI/button/MyButton";
import {IoCloseCircleSharp} from "react-icons/io5";
import Items from "./Items";

const List = (props) => {

    let [list, setList] = useState([
        "Встать",
        "Поесть",
        "Учиться",
    ]);

    const [value, setValue] = useState("");

    const appendToDoList = () => {
        setList([...list, value])
        setValue("")
    }

    const deleteItem = (item) => {
        setList(list.filter(it => it !== item))
    }

    return (
        <div className="List">
            <Items list={list} deleteItem={deleteItem} />
            {/*{list.map((item,id) => (*/}
            {/*    <div*/}
            {/*        className="Element" key={id}>{id+1}. {item}*/}
            {/*        <IoCloseCircleSharp onClick={() => deleteItem(item)} className="icon"/>*/}
            {/*    </div>*/}
            {/*))}*/}
            <div className="newPost">
            <MyInput type="text" value={value} placeholder="Добавить дело" onChange={e => setValue(e.target.value)} />
            <MyButton type="button" onClick={appendToDoList}>Добавить пункт</MyButton>
            </div>
        </div>
    );
};

export default List;
