import React from 'react';

const Login = (props) => {

    const [email, setEmail] = React.useState('');
    const [password, setPassword] = React.useState('')
    const [error, setError] = React.useState(false);

    const login = () => {
        props.users.map((user) => {
                if (user.email === email && user.password === password) {
                    setEmail("");
                    setPassword("")
                    props.setIsAuth(true);
                };
            }
        )

        setError(true);
        setEmail("");
        setPassword("")

    }


    return (
        <div>
            <h1>Login</h1>
        <form className="form">
            <input type="text" className="inpFrm" value={email} placeholder="Email" onChange={(e) => setEmail(e.target.value)} />
            <input type="password" className="inpFrm" value={password} placeholder="Password" onChange={(e) => setPassword(e.target.value)} />
            <button type="button" className="btnFrm" onClick={login}>Login</button>
            {error ?
            <div style={{color: "red", fontSize: "bold"}}>НЕ ВЕРНЫЕ ДАННЫЕ</div>
                : null
            }
        </form>
        </div>
    );
};

export default Login;
