import React, {useState} from 'react';
import {IoCloseCircleSharp, IoHammerSharp} from "react-icons/io5";
import MyInput from "./components/UI/input/MyInput";
import { IoIosAdd } from "react-icons/io";
import list from "./components/List";

const Item = (props) => {
    const [edit, setEdit] = useState(false);
    const [newItem, setNewItem] = useState("");

    const editItem = () => {
        let ind = props.list.indexOf(props.item);
        let arr= []
        for(let i = 0; i < props.list.length; i++) {
            if (i === ind) {
                arr.push(newItem);
            }
            else arr.push(list[i]);
        }
        console.log(arr);
        props.setList(arr);
        setEdit(false);
    }

    return (
        <div className="Element" key={props.id}>
            {edit ? <span>
                <input value={newItem} onChange={(e) => {setNewItem(e.target.value)}} />
                <IoIosAdd onClick={editItem}/>
                <IoCloseCircleSharp onClick={() => {setEdit(false)}}/>
                </span>
             :
                <span>{props.id+1}. {props.item}</span>
            }
            <IoCloseCircleSharp onClick={() => props.deleteItem(props.item)} className="icon"/>
            <IoHammerSharp onClick={() => {setEdit(!edit)}} className="icon"/>
        </div>
    );
};

export default Item;
